/**
 * {
    * notes: [],
    * active: null,
    * active: {
    *   id: 'bdsvjkebrfugkberldf123146654',
    *   title: '',
    *   doby: '',
    *   imageUrl: '',
    *   date:121544535456135
    * }
 * }
 */
 import { types } from '../types/types'; 

const initialState = {
     notes: [],
     active: null
}

export const notesReducer = (state = initialState, action) => {

    switch (action.type) {
        case types.notesActive: 
            return {
                ...state,
                active: {
                    ...action.payload
                }
            }

        default:
            return state;
    }
}
