import React from 'react';
import { useDispatch } from 'react-redux';
import { Link } from 'react-router-dom'
import validator from 'validator';

import { startLoginEmailPassword, starGoogleLogin } from '../../acction/auth';
import { setError } from '../../acction/ui';
import { useForm } from '../../hooks/useForm';

export const LoginScrem = () => {

    const dispatch = useDispatch();

    const [ formValues, handleInputChange ] = useForm({
        email: 'email@example.com',
        password: '123456'
    });

    const { email, password } = formValues;

    const handleLogin = (e) => {
        e.preventDefault();
        if(isFormValid()){
            dispatch( startLoginEmailPassword(email, password));
        }
    }

    const isFormValid = () => {
        if(!validator.isEmail(email)){
            dispatch(setError('Email is not valid'));
            return false;
        }else if(password.length < 5){
            dispatch(setError('The password is not valid'));
            return false;
        }
    }

    const handleGoogleLogin = () => {
        dispatch( starGoogleLogin() );
    }

    return (
        <>
            <h3 className="auth__title">Login</h3>

            <form onSubmit={ handleLogin }>

            <input 
                type="text"
                placeholder="Email"
                name="email"
                className="auth__input"
                autoComplete="off"
                value = { email }
                onChange={ handleInputChange }
                />

            <input 
                type="password"
                placeholder="Password"
                name="password"
                className="auth__input"
                value = { password }
                onChange = { handleInputChange }
                />

            <button
                type="submit"
                className="btn btn-priamry btn-block"   
            >
                Login
            </button>

                <div className="auth__social-networks">
                    <p>Login with social networks</p>
                    <div 
                        className="google-btn"
                        onClick={ handleGoogleLogin }
                    >
                        <div className="google-icon-wrapper">
                            <img className="google-icon" src="https://upload.wikimedia.org/wikipedia/commons/5/53/Google_%22G%22_Logo.svg" alt="google button" />
                        </div>
                        <p className="btn-text">
                            <b>Sign in with google</b>
                        </p>
                    </div>
                </div>

                <Link 
                    to="/auth/register"
                    className="links"
                >
                    Create new account
                </Link>
            </form>
        </>
    )
}
