import React, { useEffect, useRef } from 'react'
import { useSelector } from 'react-redux'
import { useForm } from '../../hooks/useForm';
import { NotesAppBar } from './NotesAppBar'

export const NoteScrem = () => {

    const { active:note } = useSelector( state => state.notes );
    const [ formValues, handleInputChange, reset ] = useForm(note);
    const { body, title } = formValues;

    const activeId = useRef( note.id );

    useEffect(() => {
        if(note.id !== activeId.current){
            reset(note);
        }

    }, [])

    return (
        <div className="notes__main-content">
            <NotesAppBar />

            <div className="notes__content">

                <input
                    test="text"
                    placeholder="Some awesome title"
                    className="notes__title-input"
                    autoComplete="off"
                    value={title}
                    onChange = { handleInputChange }
                />

                <textarea
                    placeholder="What happend today" 
                    className="notes__textarea"
                    onChange = { handleInputChange }
                >{body}</textarea>

                {
                    (note.url) &&
                        (
                            <div className="notes__image">
                                <img 
                                    src="https://previews.123rf.com/images/varka/varka1312/varka131201805/24677328-ilustraci%C3%B3n-del-zodiaco-signo-de-c%C3%A1ncer-como-una-muchacha-hermosa-ilustraci%C3%B3n-del-vector-.jpg"
                                    alt="imagen" 
                                />
                            </div>
                        )
                }
            </div>
        </div>
    )
}
