    }
    return (
        <>
            <h3 className="auth__title">Register</h3>

            <form onSubmit={ handleRegister }>

            <input 
                type="text"
                placeholder="Name"
                name="name"
                className="auth__input"
                autoComplete="off"
                value= {name}
                onChange = { handleInputChange }
                />

            <input 
                type="text"
                placeholder="Email"
                name="email"
                className="auth__input"
                autoComplete="off"
                value= {email}
                onChange = { handleInputChange }
                />

            <input 
                type="password"
                placeholder="Password"
                name="password"
                className="auth__input"
                value = {password}
                onChange = { handleInputChange }
                />

            <input 
                type="password"
                placeholder="Confirm password"
                name="password2"
                className="auth__input"
                value = {password2}
                onChange = { handleInputChange }
                />

            <button
                type="submit"
                className="btn btn-priamry btn-block mb-5"   
            >
                Register
            </button>


            <Link 
                to="/auth/login"
                className="links mt-5"
            >
                Already register?
            </Link>
            </form>
        </>
    )
}
